import { withStyles } from "@material-ui/core";
import Fab from "@material-ui/core/Fab";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import DeleteIcon from "@material-ui/icons/Delete";
import DoneIcon from "@material-ui/icons/Done";
import EditIcon from "@material-ui/icons/Edit";
import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import * as modalActions from "../../actions/modal";
import styles from "./styles";
class WorkItem extends Component {
	onform = (work) => {
		const { modalActionsCreators } = this.props;
        const { showModal, changeModalTitle } =
        modalActionsCreators;
        showModal();
        changeModalTitle("Sửa công việc");
		console.log(work);
	}
	render() {
		const { classes, index } = this.props;
		const { status, works } = this.props;
		const d_none = classes.d_none;
		const d_block = classes.d_block;
		const elements = works.map((work, index) => {
			return (
				<Paper key={index} className={classes.textRight}>
					<div className={classes.d_flex}>
						<h3>{work.name_work}</h3>
						<h3>{work.time}p</h3>
					</div>
					<div className={classes.d_flex}>
						<p>{work.description}</p>
					</div>
					<Fab
						className={work.status === true ? d_none : d_block}
						aria-label="delete"
					>
						<DoneIcon />
					</Fab>
					<Fab
						color="secondary"
						className={classes.mx_10}
						onClick={()=>this.onform(work)}
						aria-label="edit"
					>
						<EditIcon />
					</Fab>
					<Fab className={classes.colorError} aria-label="delete">
						<DeleteIcon />
					</Fab>
				</Paper>
			);
		});
		return (
			<Grid key={index} item xs={12} sm={6}>
				<Paper className={classes.shape}>{status.label}</Paper>
				<div className={classes.p_20}>{elements}</div>
			</Grid>
		);
	}
}
const mapStateToProps = (state) => {
    return {
        modalReduce: state.modalReduce
    };
};
const mapDispatchToProps = (dispatch) => {
    return {
        modalActionsCreators: bindActionCreators(modalActions, dispatch),
		modalEditWork: bindActionCreators(edi, dispatch)
    };
};
export default withStyles(styles)(
    connect(mapStateToProps, mapDispatchToProps)(WorkItem))
