import { withStyles } from '@material-ui/styles';
import React, { Component } from 'react';
import styles from './style';
import loadingicon from '../../assets/images/l.gif';
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import * as actionLoading from '../../actions/loading';
class GlobalLoading extends Component {
    render() {
        const {classes,showLoading} = this.props;

        return (
            <div className={classes.globalLoading}>
                <img src={loadingicon} alt="loading" className={classes.icon}/>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return {
        showLoading: state.loading.showLoading,
    };
};
const mapDispatchToProps = (dispatch) => {
    return {
        actionLoading: bindActionCreators(actionLoading,dispatch)
    };
};
export default withStyles(styles)(
    connect(mapStateToProps, mapDispatchToProps))(GlobalLoading);
