import React, { Component } from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { connect } from "react-redux";
import * as actionWorks from '../../actions/work';
import { withStyles } from "@material-ui/core";
import styles from "./styles";
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';

class workForms extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id: '',
            name_work: '',
            userId: 1,
            time: '',
            description: '',
            status: false
        };
    };

    componentWillReceiveProps(nextProps){
        console.log(this.props.workEditing);
        if(nextProps && nextProps.workEditing){  
          this.setState({
            id: nextProps.workEditing.id,
            name_work: nextProps.workEditing.name_work,
            userId: nextProps.workEditing.userId,
            time: nextProps.workEditing.time,
            description: nextProps.workEditing.description,
            status: nextProps.workEditing.status,
          });
        }
      }

    onChange = (e) => {
        const target = e.target;
        const name = target.name;
        const value = target.value;
        console.log(value);
        if(name==="status"){
           value = target.value ==='true' ? true : false;
        }
        this.setState({
            [name]: value,
        });
    }

    onSave = (e) => {
        const {workEditing} = this.props;
        e.preventDefault();
        if(workEditing && workEditing.id){
            // this.props.onUpdateWork(this.state);
            console.log(this.state);
        }else{
            // this.props.onAddWork(work);
            console.log(this.state);
        }
    }
    renderStatus(){
        let html = null;
        const {workEditing} = this.props;
        if(workEditing && workEditing.id){
            html = (
                <select className="" name="status">
                    <option>Trạng thái</option>
                    <option value={false}>Chưa hoàn thành</option>
                    <option value={true}>Đã hoàn thành</option>
                </select>
            );
        }
        return html;

    };
    render() {
        const {name_work,time,description } = this.state;

        const { open, handleClose, title ,workEditing} = this.props;

        return (
            <div>
                <Dialog
                    open={open}
                    onClose={handleClose}
                    aria-labelledby="form-dialog-title"
                >
                    <DialogTitle id="form-dialog-title">
                        {title}
                    </DialogTitle>
                    <form onSubmit={this.onSave}>
                        <DialogContent >
                            <TextField
                                autoFocus
                                margin="dense"
                                id="name"
                                label="Name"
                                type="fullname"
                                required
                                fullWidth
                                name="name_work"
                                value={name_work}
                                onChange={this.onChange}
                            />
                            <TextField
                                margin="dense"
                                id="name1"
                                label="Description"
                                type="text"
                                required
                                fullWidth
                                name="description"
                                value={description}
                                onChange={this.onChange}

                            />
                            <TextField
                                margin="dense"
                                id="name2"
                                label="Time"
                                required
                                type="number"
                                fullWidth
                                name="time"
                                value={time}
                                onChange={this.onChange}
                            />

                            {this.renderStatus()}
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={handleClose} color="primary">
                                Cancel
                            </Button>
                            <Button type="submit" color="primary">
                                {workEditing?"sửa công việc" : "thêm công việc"}
                            </Button>
                        </DialogActions>
                    </form>
                </Dialog>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return {
        workEditing: state.works.workEditing
    };
};
const mapDispatchToProps = (dispatch) => {
    return {
        onAddWork: (work) => dispatch(actionWorks.addWorks(work)),
        onUpdateWork : (work) => dispatch(actionWorks.updateWorks(work))
    };
};
export default withStyles(styles)(
    connect(mapStateToProps, mapDispatchToProps)(workForms))
