import TextField from '@material-ui/core/TextField';
import React, { Component } from "react";
import Button from '@material-ui/core/Button';
class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            email: '',
            password: '',
        };
    }

    onChange = (e)=>{
        var target = e.target;
        var name = target.name;
        var value = target.value;
        this.setState({
            [name]: value
        });
    }

    onSave = (e) => {
        e.preventDefault();

    }
    render() {
        return <div>
            <div className="login" onSubmit={this.onSave}>
                <h1>Login</h1>
                <form>
                    <TextField onChange={this.onChange} type="email" required style={{width: '100%'}} id="outlined-basic" label="Email" name="email" variant="outlined" />
                    <TextField onChange={this.onChange} type="password" required style={{width: '100%',marginTop: 20}} id="outlined-basic" label="Password" name="password" variant="outlined" />
                    <Button type="submit" style={{marginTop: 20}} variant="contained" color="primary">
                        Đăng nhập
                    </Button>
                </form>
            </div>
        </div>;
    }

}
const mapStateToProps = (state) => {
    return {
        workEditing: state.works.workEditing
    };
};
const mapDispatchToProps = (dispatch) => {
    return {
        onAddWork: (work) => dispatch(actionWorks.addWorks(work)),
        onUpdateWork : (work) => dispatch(actionWorks.updateWorks(work))
    };
};
export default withStyles(styles)(
    connect(mapStateToProps, mapDispatchToProps)(workForms))
